  '''
  @rootURL "https://news.ycombinator.com/"
  @indexURL "https://news.ycombinator.com/jobs"
  @defaultTimeout 10000

  def perform(url \\ @indexURL) do
    html = HTTPoison.get!(url, %{}, hackney: [recv_timeout: @defaultTimeout , timeout: @defaultTimeout])
    %HTTPoison.Response{body: body} = html
    Floki.find(body, ".storylink, a")
    |> scrape_each
  end

  defp scrape_each([{tag, link_attributes, texts} | tail]) do
    text = Enum.at(texts, 0)
    cond do
      is_tuple(text) -> scrape_each(tail)
      text == "More" -> perform(@rootURL <> url(link_attributes))
      Regex.match?(~r/^5 days ago/, text) -> IO.inspect "end"
      Regex.match?(~r/^.?days ago/, text) -> scrape_each(tail)
      true ->
        go_detail_or_save(url(link_attributes), text)
        scrape_each(tail)
    end
  end

  defp url([{_href, url}|_tail]), do: url

  defp go_detail_or_save(url, title) do
    cond do
    Regex.match?(~r/item\?id*/, url) ->
      detail_url = @rootURL <> url
      Show.perform(detail_url, title, true)
    Regex.match?(~r/^http*/, url) ->
      Show.perform(url, title, false)
    true ->
      IO.inspect "skip"
    end
  end
end
